def split_description(description):
    words = description.split()
    lines = []
    line = ""
    for word in words:
        if len(line) + len(word) + 1 <= 6:
            if line:
                line += " "
            line += word
        else:
            lines.append(line)
            line = word
    if line:
        lines.append(line)
    return '\n'.join(lines)
    '''words = description.split()
    lines = [' '.join(words[i:i+6]) for i in range(0, len(words), 6)]
    return '\n'.join(lines)'''


